var groupBy = (arr, prop) =>{ 
    return arr.reduce((objs, obj) => {
        const key = obj[prop];
        if (key) {
            let fi = objs.findIndex(x => x.key == key);
            if (fi>=0) {
                objs[fi].values = [...objs[fi].values, obj];
            } else {
                objs.push({
                    key: key,
                    values: [obj]
                })
            }
        }
        return objs;
    }, []);
}

