java

using System;

namespace DateDifferenceExample
{
    class Program
    {
        static void Main(string[] args)
        {
            DateTime startDate = new DateTime(2021, 01, 01);
            DateTime endDate = new DateTime(2022, 01, 01);
            
            TimeSpan diff = endDate - startDate;
            int numberOfDays = diff.Days;
            
