    Console.WriteLine("More than a year ago");
}
else if (elapsedTime.TotalDays >= 30)
{
    Console.WriteLine("About " + Math.Round(elapsedTime.TotalDays / 30) + " months ago");
}
else if (elapsedTime.TotalDays >= 1)
{
    Console.WriteLine("About " + Math.Round(elapsedTime.TotalDays) + " days ago");
}
else if (elapsedTime.TotalHours >= 1)
{
